function display_order(t) {
    var e = "";
    t.map((t) => {
        -1 == t.status
            ? (t.status = "CANCELLED")
            : 0 == t.status
            ? (t.status = "PLACED")
            : 1 == t.status
            ? (t.status = "PROCESSING")
            : 2 == t.status
            ? (t.status = "OUT FOR DELIVERY")
            : 3 == t.status && (t.status = "DELIVERED"),
            (e +=
                '<div class="order_item" id="order_id"><button class="btn" id="btn-primary">Order Id: ' +
                t._id +
                '</button><br /><button class="btn" id="btn-true">STATUS: ' +
                t.status.toUpperCase() +
                '</button><button class="btn" id="btn-false" onclick="cancel_order(\'' +
                t._id +
                "')\""),
            ("DELIVERED" != t.status &&
                "CANCELLED" != t.status &&
                "OUT FOR DELIVERY" != t.status) ||
                (e += ' style="visibility: hidden"'),
            (e +=
                '>CANCEL</button><button class="btn" id="btn-true">Payable Amount: $' +
                t.pay_amount +
                '</button><button class="btn" id="btn-primary">Payment type: ' +
                t.pay_type +
                "</button></div></div>");
    }),
        (document.getElementById("order").innerHTML = e);
}
function display_product(t) {
    var e =
        '<div class="product_head"><p id="title">' +
        t.name +
        '</p><p id="subtitle">' +
        t.details +
        '</p></div><div class="product_detail"><center><img src="' +
        t.img +
        '" alt="Product" id="product_img"/></center><button class="btn" id="btn-true">$' +
        t.price +
        '</button><button class="btn" id="btn-primary" onclick="add_cart(\'' +
        t._id +
        '\')">ADD TO CART</button><p id="subtitle">Sold By ' +
        t.soldBy +
        '</p><p id="subtitle">Specifications</p><ul>';
    (list = t.specs.split(";")),
        list.pop(),
        list.map((t) => {
            (element = t.split("=")),
                (e +=
                    "<li><b>" +
                    element[0].toUpperCase() +
                    ": </b>" +
                    element[1] +
                    "</li>");
        }),
        (e +=
            '</ul></div><div class="product_review">Product Reviews Under Construction Babe!</div>'),
        (document.getElementById("productcontent").innerHTML = e);
}
function proceed_to_pay() {
    (pay_amount = parseInt(document.getElementById("total_cost").innerText)),
        $.post("/u/pay_cart", { pay_amount: pay_amount }, function (t, e, n) {
            1 == t.success && "success" == e && go_to_web("/u/billing");
        });
}
function display_bill(t) {
    (document.getElementById("service_tax").innerText = t.service_tax),
        (document.getElementById("delivery_chrg").innerText = t.delivery_chrg),
        (document.getElementById("total_bill").innerText = (
            t.pay_amount *
            (1 + t.service_tax / 100 + t.delivery_chrg / 100)
        ).toFixed(2));
}
